data.source:
%new-style
%strict-args
%require-types
%enable-all-warnings

class TestEventSource inherits DelayedObservable {
    constructor() {
    }

    observersReady() {
        # raise a single event
        raiseEvent({"data": "constructor"});
    }

    raiseEvent(hash<auto> event_data) {
        notifyObservers("TestEventSource::raiseEvent", event_data);
    }
}

fields.config-item-1:
{"code": auto sub (auto ignored, hash record) {
        string value = get_global_config_item_value("test-item");
        value += record.test;
        return value;
     }
}
